                const displayImage = document.getElementById('displayImage');

                // Thiết lập ảnh mặc định là ảnh của nút đầu tiên
                if (options.length > 0) {
                    const defaultImage = options[0].getAttribute('data-img');
                    displayImage.style.backgroundImage = `url('${defaultImage}')`;
                }

                // Sự kiện khi hover vào các nút
                options.forEach(option => {
                    option.addEventListener('mouseenter', () => {
                        // Lấy đường dẫn hình ảnh từ thuộc tính data-img
